import React, { useState, useEffect, useRef } from 'react';
import './style.css';
import BetSearchingAPI from '../../utils/betSearchingAPI';
import { Row, Col, Table } from 'react-bootstrap';

const TableBetSlip = () => {

    const [slips, setSlips] = useState([{
        team1: '',
        team2: '',
        odds: '',
        wager: '',
        payout: '',
    }]);

    const [updatedslip, setUpdatedSlip] = useState();

    const [save, setSave] = useState(false);

    useEffect(() => {
        loadBetslips();
    }, [])

    function loadBetslips() {
        BetSearchingAPI.getBets()
            .then(res =>
                setSlips(res.data)    
            )
            .catch(err => console.log(err));
    };

    function deleteSlip(id) {
        BetSearchingAPI.deleteBet(id)
            .then(res => {
            loadBetslips();
            console.log("Slip deleted");
            })
            .catch(err => console.log(err));
    } 

    function updateSlip(e) {
        
        e.preventDefault();
        
        setSave(false);

        const betid = e.target[0].value;

        const bet = {
            team1: e.target[1].value,
            team2: e.target[2].value,
            odds: e.target[3].value,
            wager: e.target[4].value,
            payout: e.target[5].value,
        };
        
        BetSearchingAPI.updateBet(betid, bet)
            .then(res => {
                loadBetslips();
                console.log("Slip Updated");
            })
            .catch(err => console.log(err));
    }

    return (
    <>
        <Row>
            <Col>
                {save ?
                    <form id="updateForm" onSubmit={(e) => updateSlip(e)}>
                            <input type='hidden' value={updatedslip._id}></input>
                            <label className="labelUpdate">Team 1</label><input disabled value={updatedslip.team1}></input><br></br>
                            <label className="labelUpdate">Team 2</label><input disabled value={updatedslip.team2}></input><br></br>
                            <label className="labelUpdate">Odds</label><input defaultValue={updatedslip.odds}></input><br></br>
                            <label className="labelUpdate">Wager</label><input defaultValue={updatedslip.wager}></input><br></br>
                            <label className="labelUpdate">Payout</label><input defaultValue={updatedslip.payout}></input>
                            <button type="submit" class="btn btn-primary">Save</button>
                    </form> :
                <></>}
            </Col>
        </Row>
        <Row>
            <Col>
            {slips.length ? (
            <Table className="tablecontent">
                <thead>
                            <tr>
                                <th>Team 1</th>
                                <th>Team 2</th>
                                <th>Odds</th>
                                <th>Wager</th>
                                <th>Payout</th>
                                <th></th>
                            </tr>
                </thead>
            {slips.map(slip => (
        <React.Fragment key={slip._id}>
            <tbody>
                    <tr>
                    <td>{slip.team1}</td>
                    <td>{slip.team2}</td>
                    <td>{slip.odds}</td>
                    <td>{slip.wager}</td>
                    <td>{slip.payout}</td>
                    <td>
                    <button type="button" onClick={() => {
                        setUpdatedSlip(slip)
                        setSave(true);
                        }} class="btn btn-success"><svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-pencil-square" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                    <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                    </svg></button>
                    <button onClick={() => deleteSlip(slip._id)} type="button" class="btn btn-danger"><svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-trash-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5a.5.5 0 0 0-1 0v7a.5.5 0 0 0 1 0v-7z"/>
                    </svg></button>
                </td>
                </tr>
            </tbody>
        </React.Fragment>
            ))}
            </Table>
            ) : (
                <h3>No Results to Display</h3>
            )}
            </Col>
        </Row>
</>
    );
}

export default TableBetSlip;
