import React, { useState, useEffect, useRef } from 'react';
import './style.css';
import BetSearchingAPI from '../../utils/betSearchingAPI';
import { Row, Col, Table } from 'react-bootstrap';

const TableBetSlip = () => {

    const [slips, setSlips] = useState([{
        team1: '',
        team2: '',
        odds: '',
        wager: '',
        payout: '',
    }]);

    const [updatedslip, setUpdatedSlip] = ([{
        team1: '',
        team2: '',
        odds: '',
        wager: '',
        payout: ''
    }])

    const [save, setSave] = useState(null);

    const input1 = useRef('');
    const input2 = useRef('');
    const input3 = useRef('');

    useEffect(() => {
        loadBetslips();
    }, [])

    function loadBetslips() {
        BetSearchingAPI.getBets()
            .then(res =>
                setSlips(res.data)    
            )
            .catch(err => console.log(err));
    };

    function deleteSlip(id) {
        BetSearchingAPI.deleteBet(id)
            .then(res => {
            loadBetslips();
            console.log("Slip deleted");
            })
            .catch(err => console.log(err));
    } 

    function updateSlip(id, values) {
        BetSearchingAPI.updateBet(id, values)
            .then(res => {
                loadBetslips();
                console.log("Slip Updated");
            })
            .catch(err => console.log(err));
    }

    function inputHandler() {

    }

    return (
    
        <Row>
            <Col>
            {slips.length ? (
            <Table>
                <thead>
                            <tr>
                                <th>Team 1</th>
                                <th>Team 2</th>
                                <th>Odds</th>
                                <th>Wager</th>
                                <th>Payout</th>
                                <th></th>
                            </tr>
