                            <input disabled value={slip.team1}></input>
                            <input disabled value={slip.team2}></input>
                            <input ref={input1} defaultValue={slip.odds}></input>
                            <input ref={input2} defaultValue={slip.wager}></input>
                            <input ref={input3} defaultValue={slip.payout}></input>
                            <button type="button" onClick={() => {
                            console.log("Input 1:" , input1.value);
                            updateSlip(slip._id, {odds: input1.value, wager: input2.value, payout: input3.value})
                            setSave(null);
                            }} class="btn btn-success"><svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-pencil-square" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
