            <React.Fragment key={slip._id}>
                <tbody>
                <tr>
                    <td>{slip.team1}</td>
                    <td>{slip.team2}</td>
                    <td>{slip.odds}</td>
                    <td>{slip.wager}</td>
                    <td>{slip.payout}</td>
                    <td>
                <button type="button" onClick={() => setSave(slip._id)} class="btn btn-success"><svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-pencil-square" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
