import React, { useState, useEffect } from 'react';


const TableBetSlip = () => {
    
    const [slips, setSlips] = useState([{
        team1: '',
        team2: '',
        odds: '',
        wager: '',
        payout: '',
    }]);

    useEffect(() => {
        fetch("/api/betslips").then(res => {
            if(res.ok) {
                return res.json()
            }
        }).then(jsonRes => setSlips(jsonRes));
    })

    return (
        
    <div class="row">
        <div class="col-12">
            <table class="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">Team 1</th>
                        <th scope="col">Team 2</th>
                        <th scope="col">Odds</th>
                        <th scope="col">Wager</th>
                        <th scope="col">Payout</th>
                    </tr>
                </thead>

            {slips.map(slip =>
                <tbody>
                    <tr>
                        <td>{slip.team1}</td>
                        <td>{slip.team2}</td>
                        <td>{slip.odds}</td>
                        <td>{slip.wager}</td>
                        <td>{slip.payout}</td>
                        <td>
                        <button type="button" class="btn btn-success"><i class="bi bi-pencil-square"></i></button>
                        <button type="button" class="btn btn-danger"><i class="far fa-trash-alt"></i></button>
                        </td>
                    </tr>
                </tbody>
            )}
            </table>
        </div>
    </div>

    )


}

export default TableBetSlip;
